	function listing($tableName,$round_master_id)
    {
        $this->db->select('a.id, a.death_date, a.death_time, a.fk_death_cause, a.fk_death_place, a.household_master_id, a.member_master_id, a.round_master_id,
		member_code,member_name, birth_date, household_code,inv_status');
        $this->db->from($tableName .' as a');
        $this->db->join('tbl_member_master as m','m.id= a.member_master_id');
        $this->db->join('household_master as h','h.id= a.household_master_id');
        $this->db->where('a.round_master_id',$round_master_id);
        $query = $this->db->get();
        
        return $query->result();
    }
	
	
    
    function column_names_adult($tableName)
    {
        $this->db->select('a.*,member_code,member_name, birth_date, household_code,NAME'); 
        $this->db->from($tableName .' as a');
        $this->db->join('tbl_member_master as m','m.id= a.member_master_id');
        $this->db->join('household_master as h','h.id= a.household_master_id');
        $this->db->join('tbl_users as ui','ui.userId= a.insertedBy');
        $this->db->where('a.VA_TYPE',1);
        $query = $this->db->get();

        return $query->list_fields();
    }
    
    function adultListing($tableName)
    {
        $this->db->select('a.*,member_code,member_name, birth_date, household_code,NAME,household_master_id,'
                . 'de.Q7_24_1_A,de.Q7_24_2_A,de.Q7_24_3_A,de.Q7_24_4_A,de.Q7_24_5_A,de.Q7_24_6_A,de.Q7_24_7_A,de.Q7_24_8_A'
                .',I_A,I_B,I_C,I_D,II_A,II_B,verbal_autopsy_specialist_name'
                ); 
        $this->db->from($tableName .' as a');
        $this->db->join('member_death_extended as de', 'de.member_death_id= a.ID');
        $this->db->join('tbl_member_master as m','m.id= a.member_master_id');
        $this->db->join('household_master as h','h.id= a.household_master_id');
        $this->db->join('tbl_users as ui','ui.userId= a.insertedBy');
        $this->db->where('a.VA_TYPE',1);
		
        $query = $this->db->get();

        return $query->result();
    }
    
