	function getListInfo($id,$tableName)
	{
        $this->db->select($tableName.'.*,member_death_extended.*,'.'member_master.fk_sex,sex.NAME as SEX, sex.code as sexCode, member_master.member_code, 
		member_master.member_name,birth_date,household_code,fk_marital_status,fk_main_occupation');
        $this->db->from($tableName);
        $this->db->join('tbl_member_master as member_master', 'member_master.id='.$tableName.'.member_master_id','left');
        $this->db->join('lookup_details_va as sex', 'sex.ID=member_master.fk_sex','left');
        $this->db->join('household_master', 'household_master.id=member_master.household_master_id_hh','left');
        $this->db->join('tbl_member_occupation as member_occupation', 'member_occupation.id=member_master.fk_occupation_id_last','left');
		$this->db->join('lookup_details_va as occu', 'occu.ID=member_occupation.fk_main_occupation','left');
		$this->db->join('member_death_extended', 'member_death_extended.member_death_id='.$tableName.'.id','left');
        $this->db->where($tableName.'.id',$id);
        $query = $this->db->get();
        return $query->row();
    }
	
	

    
    function updateInfo($IDInfo, $member_death_table_id,$table) {
    $this->db->where('ID', $member_death_table_id);
    return $this->db->update($table,$IDInfo);
    }
    
    function updateExtendedTable($IDInfo, $member_death_table_id, $table) {
        $this->db->where('member_death_id', $member_death_table_id);
        return $this->db->update($table, $IDInfo);
    }
    
    function InsertInfo($IDInfo, $table) {
        return $this->db->insert($table, $IDInfo);
    }

    function getLookUpList($lookup_master_code)
    {
        $this->db->select('a.ID as id, a.CODE as code, a.NAME as name');
        $this->db->from('lookup_details_va as a');
        $this->db->join('lookup_master_va b', 'b.ID = a.LOOKUP_MASTER_ID', 'inner');
        $this->db->where('b.CODE', $lookup_master_code);
        $this->db->where('a.ACTIVE', 'Yes');
        $this->db->order_by('a.DISPLAY_ORDER', 'asc');
       
        $query = $this->db->get();
		
        return $query->result();
    }
    
    function NeonateListing($tableName)
    {
        $this->db->select('a.*,member_code,member_name, birth_date, household_code,NAME,household_master_id,neonatal_A,neonatal_B,maternal_C,maternal_D,others_E,verbal_autopsy_specialist_name');
        $this->db->from($tableName .' as a');
        $this->db->join('member_death_extended as de', 'de.member_death_id= a.ID');
        $this->db->join('tbl_member_master as m','m.id= a.member_master_id');
        $this->db->join('household_master as h','h.id= a.household_master_id');
        $this->db->join('tbl_users as ui','ui.userId= a.insertedBy');
        $this->db->where('a.VA_TYPE',3);
        $query = $this->db->get();

        return $query->result();
    }


