    function getNeonateDropdown($fieldsData,$textfield,$dateField,$textarea)
    {
        $dropdown = [];

        foreach ($fieldsData as $key => $value) {

             if(!in_array($value, $textfield) && !in_array($value, $dateField) && !in_array($value, $textarea)){
                $dropdown[] = $value;
            }
        }

        return $dropdown;
    }

